function addQuotes(inputString) {
    // Regular expression to match "Author": "Quote"
    const authorQuoteRegex = /"([^"]+)"\s*:\s*"([^"]+)"/g;
    let match;
    const authorQuotesMap = {}; // Object to store authors and their quotes
    const promises = []; // Array to hold promises from Firebase operations

    // Extract each author and quote from the input string
    while ((match = authorQuoteRegex.exec(inputString)) !== null) {
        const author = match[1];  // Get the author name from the match
        const quote = match[2];   // Get the quote from the match

        // Ensure that author exists in the authorQuotesMap
        if (!authorQuotesMap[author]) {
            authorQuotesMap[author] = []; // Initialize an empty array for quotes
        }

        // Add the quote to the author's array in authorQuotesMap
        authorQuotesMap[author].push(quote);
    }

    // Iterate over each author and add the quotes to Firebase
    Object.keys(authorQuotesMap).forEach(author => {
        // Query to find the author by "name" field in Firebase
        const authorQuery = database.ref('Authors').orderByChild('name').equalTo(author);

        // Add a new promise to the array for each Firebase operation
        const promise = authorQuery.once('value').then(function(snapshot) {
            if (snapshot.exists()) {
                snapshot.forEach(function(childSnapshot) {
                    // Found the author, get the reference to the author node
                    const authorRef = childSnapshot.ref;

                    // Get the existing Quotes object or initialize an empty one
                    const authorData = childSnapshot.val();
                    const existingQuotes = authorData.Quotes || {};

                    // Merge the new quotes with existing quotes
                    authorQuotesMap[author].forEach((quoteText, index) => {
                        const parentKey = index.toString(); // Use the index as the parent key
                        
                        // Add or update the quote with an 'index' field matching the parent key
                        existingQuotes[parentKey] = {
                            Value: quoteText,
                             index: parentKey, // Ensure the index field matches the parent key
                    Comments: []  // Initialize Comments as an empty array
                        };
                    });

                    // Update the Quotes in Firebase with the new structure
                    return authorRef.update({ Quotes: existingQuotes }).then(() => {
                        console.log(`Added quotes and Comments to ${author}'s Quotes array.`);
                    }).catch((error) => {
                        console.error("Error updating quotes: ", error);
                    });
                });
            } else {
                console.log(`Author: "${author}" not found in the database.`);
            }
        }).catch(function(error) {
            console.error("Error fetching data for author: ", error);
        });

        // Push the promise into the array
        promises.push(promise);
    });

    // Wait for all Firebase operations to complete
    Promise.all(promises).then(() => {
        console.log("All Quotes Updated with Comments field and matching index.");
    }).catch((error) => {
        console.error("Error adding quotes: ", error);
    });
}


