// Define authorsRef
const authorsRef = database.ref('Authors');

function addCommentsArrayToQuotes(person) {

    // Validate the person parameter
    if (!person) {
        console.error('The person parameter is undefined or null.');
        return;
    }
    
    // Fetch the author by name
    authorsRef.orderByChild('name').equalTo(person).once('value', snapshot => {
        if (snapshot.exists()) {
            snapshot.forEach(childSnapshot => {
                const authorKey = childSnapshot.key;  // Unique key for the author
                const authorData = childSnapshot.val();
                const quotes = authorData.Quotes || {};

                // Loop through each quote and add an empty Comments array if it doesn't exist
                Object.keys(quotes).forEach((quoteIndex) => {
                    const quote = quotes[quoteIndex];

                    // Ensure Comments array exists for each quote
                    if (!quote.Comments) {
                        database.ref(`Authors/${authorKey}/Quotes/${quoteIndex}/Comments`).set([])
                            .then(() => {
                                console.log(`Added empty Comments array for quote ${quoteIndex} of author ${authorKey}`);
                            })
                            .catch(error => {
                                console.error(`Failed to add Comments array for quote ${quoteIndex} of author ${authorKey}: `, error);
                            });
                    }
                });
            });
        } else {
            console.error(`No author found with the name ${person}`);
        }
    }).catch(error => {
        console.error('Error fetching author:', error);
    });
}
